function Option({ question, answer, dispatch }) {
  return (
    <div className="options">
      {question.options.map((option, index) => (
        <button className={`btn btn-option ${answer === index ? "" : "" }`} key={option} onClick={()=> dispatch({type: "answer", payload: index})}>
          {option}
        </button>
      ))}
    </div>
  );
}

export default Option;
