import { useEffect } from "react";

function Timer({ dispatch }){
    const mins = Math.floor(secondsRemaining / 60);
  const seconds = secondsRemaining % 60;

    useEffect(()=>{
        const id = setInterval(function () {
            dispatch({ type: "tick" });
          }, 1000);
    },[dispatch])

    return (
        <div className="timer">
          {mins < 10 && "0"}
          {mins}:{seconds < 10 && "0"}
          {seconds}
        </div>
      );
}

export default Timer
